const Options=require("./Options"),Result=require("./Result"),RegexValidator=require("./RegexValidator"),Comparator=require("./Comparator");class JSONCompare{constructor(t={}){this.options=new Options(t),this.result=new Result,this.regexValidator=new RegexValidator(this.options,this.result),this.comparator=new Comparator(this.options,this.result,this.regexValidator)}compare(t,e){return this.result.reset(),this.comparator.compareObjects(t,e,""),this.result.updateSummary(),this.result.getResult()}compareAndValidate(t,e){return this.compare(t,e),this.regexValidator.validateAllMatchingKeys(e),this.result.updateSummary(),this.result.getResult()}}module.exports=JSONCompare;